import React from 'react';

//----- CircularProgressbar ----- 
//referen: https://www.npmjs.com/package/react-circular-progressbar
import { CircularProgressbar, buildStyles } from 'react-circular-progressbar';
import 'react-circular-progressbar/dist/styles.css';

//----- Button components -----
import PauseButton from './PauseButton';
import PlayButton from './PlayButton';
import SettingsButton from './SettingsButtton';

// const red = '#f54e4e';
// const green = '#4aec8c';

function Timer() {
    const percentage = 60;

    return (
        <div>
            <h1>This is my timer</h1>

            {/* ---- Progressbar ---- */}

            <CircularProgressbar value={percentage} text={`${60}%`} styles={buildStyles({
                textColor: "lightblue",
                pathColor: "lightblue",
                trailColor: "lightgray"

            })} />

            {/* ---- Pay-Pause button component ---- */}
            <div style={{ marginTop: '20px' }}>
                <PlayButton />
                <PauseButton />
            </div>


            {/* ---- Settings button component ---- */}
            <div style={{ marginTop: '20px' }}>
                <SettingsButton />
            </div>


        </div>
    );
}

export default Timer;
