    <main>
      {/*----- using context -----*/}
      <SettingsContext.Provider value={{
        workMinutes
      }} >


      {/* Settings & Timer components */}
      {/* if showSettings is true show the Settings, otherwise, show Timer */}
      {showSettings ? <Settings /> : <Timer />}

      </SettingsContext.Provider>


    </main>
  );
}

export default App;
